    @Override
    public void onLoad() {
        final PartType partType = PartType.TWO;

        switch (partType) {
            case ONE -> this.partOne();
            case TWO -> this.partTwo();
        }
    }

    private int floor(String input) {
        final AtomicInteger index = new AtomicInteger();

        IntStream.range(0, input.length()).forEachOrdered(i -> {
            final char character = input.charAt(i);

            if (character == '(')
                index.getAndIncrement();
            else if (character == ')')
                index.getAndDecrement();
        });

        return index.get();
    }

    private void partOne() {
        this.logger.info("Floor: " + this.floor(input));
    }

    private int position(String input) {
        final AtomicInteger index = new AtomicInteger();
        final char[] characters = input.toCharArray();

        IntStream.range(0, characters.length).forEachOrdered(i -> {
            if (characters[i] == '(')
                index.getAndIncrement();
            else if (characters[i] == ')')
                index.getAndDecrement();

            if (index.get() == -1)
                index.set(i + 1);
        });

        return index.get();
    }

    private void partTwo() {
        this.logger.info("Position: " + this.position(input));
    }
